-- View: tổng thu chi theo người dùng
CREATE VIEW [dbo].[TongHopThuChi]
AS
SELECT 
    nd.MaNguoiDung,
    nd.HoTen,
    dm.Loai,
    SUM(gd.SoTien) AS TongTien
FROM NguoiDung nd
INNER JOIN GiaoDich gd ON nd.MaNguoiDung = gd.MaNguoiDung
INNER JOIN DanhMuc dm ON gd.MaDanhMuc = dm.MaDanhMuc
GROUP BY nd.MaNguoiDung, nd.HoTen, dm.Loai;
GO
