-- View: chi tiết giao dịch (theo thứ tự yêu cầu + ngày dd/MM/yyyy)
CREATE VIEW [dbo].[V_GiaoDich_ChiTiet] AS
SELECT 
    gd.MaGiaoDich,
    dm.TenDanhMuc,
    gd.MoTa,
    CONVERT(VARCHAR(10), gd.NgayGD, 103) AS NgayGD, -- dd/MM/yyyy
    gd.SoTien,
    dm.Loai,
    nd.HoTen
FROM GiaoDich gd
JOIN DanhMuc dm ON gd.MaDanhMuc = dm.MaDanhMuc
JOIN NguoiDung nd ON gd.MaNguoiDung = nd.MaNguoiDung;
